import React, { useState } from 'react';
import axios from 'axios';
import './App.css';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip as ChartTooltip,
  Legend,
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, ChartTooltip, Legend);

function App() {
  const [summary, setSummary] = useState('');
  const [parts, setParts] = useState([]);
  const [chartData, setChartData] = useState(null);

  const handleUpload = async (e) => {
    e.preventDefault();
    const file = e.target.logfile.files[0];
    const formData = new FormData();
    formData.append("file", file);
    const res = await axios.post('/api/upload', formData);
    setSummary(res.data.summary);
    setParts(res.data.parts);

    // Prepare chart data (e.g., count error types in tooltips)
    const errorCounts = {};
    res.data.parts.forEach(part => {
      // Try to extract a type_of_error if present
      if (part.type_of_error) {
        errorCounts[part.type_of_error] = (errorCounts[part.type_of_error] || 0) + 1;
      } else {
        // fallback: count by tooltip content
        const key = part.tooltip.split(':')[0];
        errorCounts[key] = (errorCounts[key] || 0) + 1;
      }
    });
    setChartData({
      labels: Object.keys(errorCounts),
      datasets: [
        {
          label: 'Error Frequency',
          data: Object.values(errorCounts),
          backgroundColor: [
            '#3a7bd5', '#00d2ff', '#f7971e', '#f44336', '#8e54e9', '#43cea2', '#fc354c', '#292E49', '#11998e', '#38ef7d'
          ],
        },
      ],
    });
  };

  return (
    <div className="app-container">
      <h1>ðŸ“˜ Log Analyzer</h1>
      <form className="upload-section" onSubmit={handleUpload}>
        <input type="file" name="logfile" />
        <button type="submit">Analyze</button>
      </form>
      <div className="summary-section">
        <h2>ðŸ§  Summary</h2>
        <p>{summary}</p>
      </div>
      <div className="parts-section">
        <h2>ðŸ“Œ Parts</h2>
        <ul className="parts-list">
          {parts.map((part, idx) => (
            <li key={idx}>
              <span style={{textDecoration: 'underline dotted', cursor: 'pointer'}}>
                {part.summary_start_index !== undefined && part.summary_end_index !== undefined
                  ? summary.slice(part.summary_start_index, part.summary_end_index)
                  : `Part ${idx+1}`}
                <span className="tooltip">{part.tooltip}</span>
              </span>
            </li>
          ))}
        </ul>
      </div>
      {chartData && (
        <div className="chart-section">
          <h2>ðŸ“Š Error/Info Frequency</h2>
          <Bar
            data={chartData}
            options={{
              responsive: true,
              plugins: {
                legend: { display: false },
                title: { display: false },
                tooltip: { enabled: true }
              },
              scales: {
                y: { beginAtZero: true, ticks: { stepSize: 1 } },
              },
            }}
            height={280}
          />
        </div>
      )}
    </div>
  );
}

export default App;
