import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

export default function BetterChart({ data }) {
  if (!data || !data.labels || data.labels.length === 0) {
    return <div style={{color:'#888',padding:'20px'}}>No chart data to display.</div>;
  }

  return (
    <div className="better-chart-container">
      <Bar
        data={data}
        options={{
          responsive: true,
          plugins: {
            legend: {
              display: true,
              position: 'top',
              labels: {
                font: { size: 14, family: 'Segoe UI, Arial' },
                color: '#3a7bd5',
              },
            },
            title: {
              display: true,
              text: 'Error/Info Frequency',
              font: { size: 20, family: 'Segoe UI, Arial' },
              color: '#3a7bd5',
              padding: {top: 10, bottom: 20},
            },
            tooltip: {
              enabled: true,
              backgroundColor: '#3a7bd5',
              titleColor: '#fff',
              bodyColor: '#fff',
              borderColor: '#00d2ff',
              borderWidth: 1,
              caretSize: 8,
              padding: 12,
              displayColors: true,
              callbacks: {
                title: function(context) {
                  // Show full label/title
                  return context[0].label || '';
                },
                label: function(context) {
                  // Show full value with label
                  let label = context.dataset.label || '';
                  if (label) label += ': ';
                  label += context.parsed.y !== undefined ? context.parsed.y : context.parsed;
                  return label;
                }
              },
              bodyFont: { size: 15, family: 'Segoe UI, Arial' },
              titleFont: { size: 16, family: 'Segoe UI, Arial', weight: 'bold' },
              boxPadding: 6,
              cornerRadius: 8,
              caretPadding: 6,
              // Try to prevent overflow
              rtl: false,
              textDirection: 'ltr',
              multiKeyBackground: '#00d2ff',
            },
          },
          scales: {
            x: {
              ticks: { color: '#3a7bd5', font: {size: 13} },
              grid: { color: '#e0eafc' },
            },
            y: {
              beginAtZero: true,
              ticks: { color: '#3a7bd5', font: {size: 13} },
              grid: { color: '#e0eafc' },
            },
          },
        }}
        height={320}
      />
    </div>
  );
}
